import * as _ from 'lamb';

import {isObject, isString} from '@svizzle/utils';

import {ifExistsGet} from 'app/utils/generic';

export const getESType = _.adapter([
	_.casus(isString, _.identity),
	_.casus(isObject, _.adapter([
		ifExistsGet('es_type'),
		ifExistsGet('data_type'),
		ifExistsGet('type'),
	])),
	_.always('object')
]);
